function keyPress (event) {
    if (event.keyCode == 87) {
        console.log('w')
    }
    if (event.keyCode == 65) {
        console.log('a') 
    }
    if (event.keyCode== 83) {
        console.log('s')
    }
    if (event.keyCode == 68) {
        console.log('d')
    }
}
